//imports the Web3 API
import Web3 from 'web3';

/**
 * creates & exports new instance for 
 * Web3 using provided service by Metamask.
 */

const rpcURL = "https://ropsten.infura.io/v3/cf658270146645bca881f8a7d4752099" // Your RCP URL goes here

var web3;
var instance = window.ethereum;
window.addEventListener('load', async () => {
    if (instance) {
        try {
            // Request account access if needed
            await instance.enable();
            // Acccounts now exposed
            web3 = new Web3(instance);
            console.log("enable");
        } catch (error) {
            // User denied account access...
            alert('Please allow access for the app to work');
        }
    } else if (window.web3) {
        web3 = new Web3(web3.currentProvider);
        console.log("Metamask");
        // Acccounts always exposed
    }
    else {
        console.log('Non-Ethereum browser detected. You should consider trying MetaMask!');
    }
});
