    //     console.log('Non-Ethereum browser detected. You should consider trying MetaMask!');
    // }
});
web3.eth.getBalance(web3.eth.accounts[0], function(error, result){
    if (error) {
        console.error(error)
    } else {
        console.log(web3.fromWei(result.toNumber()));
    }
});
export default web3;
