import Web3 from 'web3';

const rpcURL = "https://ropsten.infura.io/v3/cf658270146645bca881f8a7d4752099" // Your RCP URL goes here
let currentWeb3;

window.addEventListener('load', async () => {
    const metaMaskEthProvider = window.ethereum;
    if (metaMaskEthProvider) {
        let instance = new Web3(metaMaskEthProvider);
        try {
            await metaMaskEthProvider.enable();
            currentWeb3 = instance;
        } catch (error) {
            alert('Allow access for the app to work');
        }
    } else {
        console.log('Non-Ethereum browser detected. You should consider trying MetaMask!');
        currentWeb3 = new Web3(rpcURL);
    }
});
export default currentWeb3;
